export const UNIT_TYPES = Object.keys(UNIT_DEFINITIONS);
export function getUnitDefinition(type) {
    return UNIT_DEFINITIONS[type];
}
export function getUnitsProducibleAt(property) {
    switch (property) {
        case 'base':
            return [
                'infantry',
                'mech',
                'recon',
                'apc',
                'tank',
                'md_tank',
                'neotank',
                'anti_air',
                'artillery',
                'rockets',
                'missiles',
            ];
        case 'airport':
            return ['fighter', 'bomber', 'b_copter', 't_copter'];
        case 'port':
            return ['battleship', 'sub', 'cruiser', 'lander'];
        default:
            return [];
    }
}
